import React, { useState } from 'react';
import styled from 'styled-components';
import { Card, Form, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const StyledFlexContainer = styled.div`
  display: flex;
  justify-content: space-around;
  margin-top: 20px;
  margin-bottom: 100px;
`;

const ViagemForm = () => {
  const [origem, setOrigem] = useState('');
  const [destino, setDestino] = useState('');
  const [dataIda, setDataIda] = useState('');
  const [dataVolta, setDataVolta] = useState('');
  const [numPassageiros, setNumPassageiros] = useState(1);
  const [tipoViagem, setTipoViagem] = useState('idaVolta');
  const [etapaAtual, setEtapaAtual] = useState(1);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (etapaAtual === 1) {
      setEtapaAtual(2);
    } else {
      // Lógica para lidar com os dados do formulário, por exemplo, enviar para o servidor.
      console.log('Origem:', origem);
      console.log('Destino:', destino);
      console.log('Data de Ida:', dataIda);
      console.log('Data de Volta:', dataVolta);
      console.log('Número de Passageiros:', numPassageiros);
      console.log('Tipo de Viagem:', tipoViagem);
      // Pode redirecionar para a próxima página ou executar outras ações aqui
    }
  };

  return (
    <StyledFlexContainer>
      {etapaAtual === 1 && (
        <>
          <Card style={{ width: '18rem' }}>
            <Card.Body>
              <Card.Title>Origem</Card.Title>
              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                  <Form.Label>Origem:</Form.Label>
                  <Form.Control
                    type="text"
                    value={origem}
                    onChange={(e) => setOrigem(e.target.value)}
                    required
                  />
                </Form.Group>
              </Form>
            </Card.Body>
          </Card>

          {/* Adicione mais etapas conforme necessário */}
        </>
      )}

      {etapaAtual === 2 && (
        <>
          <Card style={{ width: '18rem' }}>
            <Card.Body>
              <Card.Title>Destino</Card.Title>
              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                  <Form.Label>Destino:</Form.Label>
                  <Form.Control
                    type="text"
                    value={destino}
                    onChange={(e) => setDestino(e.target.value)}
                    required
                  />
                </Form.Group>
              </Form>
            </Card.Body>
          </Card>

          {/* Adicione mais etapas conforme necessário */}
        </>
      )}

      {/* Botão para continuar */}
      <Card style={{ width: '18rem' }}>
        <Card.Body>
          <Button variant="primary" type="submit" onClick={handleSubmit}>
            {etapaAtual === 1 ? 'Próximo' : 'Finalizar'}
          </Button>
        </Card.Body>
      </Card>
    </StyledFlexContainer>
  );
};

export default ViagemForm;
